CREATE OR REPLACE FUNCTION public.check_account_entry_type()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    -- Vrifier que le montant correspond au type (crdit/dbit)
    IF EXISTS (
        SELECT 1 
        FROM account_entry_types 
        WHERE id = NEW.entry_type_id 
        AND (
            (is_credit = true AND NEW.amount < 0) OR
            (is_credit = false AND NEW.amount > 0)
        )
    ) THEN
        RAISE EXCEPTION 'Le montant ne correspond pas au type d''opration (crdit/dbit)';
    END IF;

    RETURN NEW;
END;
$function$

CREATE OR REPLACE FUNCTION public.check_auth_tables()
 RETURNS boolean
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
begin
  return exists (
    select from information_schema.tables 
    where table_schema = 'auth' 
    and table_name = 'users'
  );
end;
$function$

CREATE OR REPLACE FUNCTION public.check_availability_conflicts()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    -- Check for overlapping availabilities with the same slot_type
    IF EXISTS (
        SELECT 1 FROM availabilities
        WHERE id != COALESCE(NEW.id, '00000000-0000-0000-0000-000000000000')
        AND (
            (NEW.user_id IS NOT NULL AND user_id = NEW.user_id) OR
            (NEW.aircraft_id IS NOT NULL AND aircraft_id = NEW.aircraft_id)
        )
        AND slot_type = NEW.slot_type
        AND (
            (NEW.start_time, NEW.end_time) OVERLAPS (start_time, end_time)
        )
    ) THEN
        RAISE EXCEPTION 'Availability conflict detected for the same slot type';
    END IF;

    RETURN NEW;
END;
$function$

CREATE OR REPLACE FUNCTION public.create_club_with_admin(p_club_name text, p_club_code text, p_admin_email text, p_admin_password text, p_admin_login text, p_admin_first_name text, p_admin_last_name text)
 RETURNS uuid
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
  v_club_id uuid;
  v_user_id uuid;
  v_existing_user_id uuid;
BEGIN
  -- Check if email already exists
  SELECT id INTO v_existing_user_id
  FROM public.users
  WHERE email = p_admin_email;

  IF v_existing_user_id IS NOT NULL THEN
    RAISE EXCEPTION 'Un utilisateur avec cet email existe dj';
  END IF;

  -- Validate club code format
  IF NOT p_club_code ~ '^[A-Za-z0-9]{3,10}$' THEN
    RAISE EXCEPTION 'Le code de l''arodrome doit contenir entre 3 et 10 caractres alphanumriques';
  END IF;

  -- Create the club
  INSERT INTO public.clubs (name, code)
  VALUES (p_club_name, upper(p_club_code))
  RETURNING id INTO v_club_id;

  -- 1. Create user in public.users
  INSERT INTO public.users (
    first_name,
    last_name,
    email,
    login,
    created_at,
    updated_at
  )
  VALUES (
    p_admin_first_name,
    p_admin_last_name,
    p_admin_email,
    p_admin_login,
    NOW(),
    NOW()
  )
  RETURNING id INTO v_user_id;

  -- 2. Create auth user
  PERFORM create_auth_user(
    p_email := p_admin_email,
    p_password := p_admin_password,
    p_role := 'ADMIN',
    p_login := p_admin_login,
    p_user_id := v_user_id
  );

  -- 3. Update user groups to add admin role
  PERFORM update_user_groups(
    p_user_id := v_user_id,
    p_groups := ARRAY['ADMIN']::text[]
  );

  -- 4. Add admin as club member
  INSERT INTO club_members (
    user_id,
    club_id,
    joined_at
  )
  VALUES (
    v_user_id,
    v_club_id,
    NOW()
  );

  RETURN v_club_id;

EXCEPTION WHEN OTHERS THEN
  -- If any error occurs, clean up by deleting the club if it was created
  IF v_club_id IS NOT NULL THEN
    DELETE FROM public.clubs WHERE id = v_club_id;
  END IF;
  -- And the user if it was created
  IF v_user_id IS NOT NULL THEN
    DELETE FROM public.users WHERE id = v_user_id;
  END IF;
  RAISE;
END;
$function$

CREATE OR REPLACE FUNCTION public.create_instructor_student_chat(instructor_id uuid, student_id uuid)
 RETURNS uuid
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
DECLARE
    room_id uuid;
BEGIN
    -- Check if instructor is actually an instructor
    IF NOT EXISTS (
        SELECT 1 FROM users 
        WHERE id = instructor_id AND role = 'INSTRUCTOR'
    ) THEN
        RAISE EXCEPTION 'Invalid instructor ID';
    END IF;

    -- Create the chat room
    INSERT INTO chat_rooms (name, type)
    VALUES (
        'Chat instructeur-lve',
        'INSTRUCTOR_STUDENT'
    )
    RETURNING id INTO room_id;

    -- Add both users to the room
    INSERT INTO chat_room_members (room_id, user_id)
    VALUES
        (room_id, instructor_id),
        (room_id, student_id);

    RETURN room_id;
END;
$function$

CREATE OR REPLACE FUNCTION public.delete_auth_user()
 RETURNS trigger
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
begin
  -- Supprimer l'utilisateur de auth.users
  delete from auth.users where id = old.id;
  return old;
exception
  when others then
    -- Log l'erreur mais ne bloque pas la suppression
    raise warning 'Erreur lors de la suppression de l''utilisateur dans auth.users: %', SQLERRM;
    return old;
end;
$function$

CREATE OR REPLACE FUNCTION public.drop_auth_constraint()
 RETURNS void
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
begin
  -- Supprimer la contrainte de public.users vers auth.users
  alter table public.users drop constraint if exists users_auth_id_fkey;

  -- Supprimer la contrainte de auth.identities vers auth.users
  alter table auth.identities drop constraint if exists identities_user_id_fkey;

  -- Supprimer autres contraintes potentielles
  alter table auth.sessions drop constraint if exists sessions_user_id_fkey;
  alter table auth.mfa_factors drop constraint if exists mfa_factors_user_id_fkey;
  alter table auth.refresh_tokens drop constraint if exists refresh_tokens_user_id_fkey;
end;
$function$

CREATE OR REPLACE FUNCTION public.execute_sql(query text)
 RETURNS json
 LANGUAGE plpgsql
 SECURITY DEFINER
 SET search_path TO 'public'
AS $function$
declare
  result json;
  current_user_id uuid;
begin
  -- Rcuprer l'ID de l'utilisateur courant
  current_user_id := auth.uid();

  -- Log pour debug
  raise notice 'Current user ID: %', current_user_id;

  -- Remplacer les occurrences de auth.uid() dans la requte
  query := replace(query, 'auth.uid()', '''' || current_user_id || '''::uuid');

  -- Excuter la requte modifie
  execute format('select json_agg(t) from (%s) t', query) into result;

  return coalesce(result, '[]'::json);
exception when others then
  return json_build_object(
    'error', SQLERRM,
    'detail', SQLSTATE,
    'query', query,
    'user_id', current_user_id
  );
end;
$function$

CREATE OR REPLACE FUNCTION public.get_user_role(user_id uuid)
 RETURNS text
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
BEGIN
    IF is_member_of_group(user_id, 'Administrateurs') THEN
        RETURN 'ADMIN';
    ELSIF is_member_of_group(user_id, 'Instructeurs') THEN
        RETURN 'INSTRUCTOR';
    ELSIF is_member_of_group(user_id, 'Mcaniciens') THEN
        RETURN 'MECHANIC';
    ELSIF is_member_of_group(user_id, 'Pilotes') THEN
        RETURN 'PILOT';
    ELSE
        RETURN 'PILOT'; -- Rle par dfaut
    END IF;
END;
$function$

CREATE OR REPLACE FUNCTION public.handle_updated_at()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
begin
    new.updated_at = now();
    return new;
end;
$function$

CREATE OR REPLACE FUNCTION public.migrate_roles_to_groups()
 RETURNS void
 LANGUAGE plpgsql
AS $function$
DECLARE
    pilot_group_id UUID;
    instructor_group_id UUID;
    admin_group_id UUID;
    mechanic_group_id UUID;
BEGIN
    -- Get group IDs
    SELECT id INTO pilot_group_id FROM user_groups WHERE name = 'Pilotes';
    SELECT id INTO instructor_group_id FROM user_groups WHERE name = 'Instructeurs';
    SELECT id INTO admin_group_id FROM user_groups WHERE name = 'Administrateurs';
    SELECT id INTO mechanic_group_id FROM user_groups WHERE name = 'Mcaniciens';

    -- Migrate PILOT role
    INSERT INTO user_group_memberships (user_id, group_id)
    SELECT id, pilot_group_id
    FROM users
    WHERE role = 'PILOT';

    -- Migrate INSTRUCTOR role
    INSERT INTO user_group_memberships (user_id, group_id)
    SELECT id, instructor_group_id
    FROM users
    WHERE role = 'INSTRUCTOR';

    -- Migrate ADMIN role
    INSERT INTO user_group_memberships (user_id, group_id)
    SELECT id, admin_group_id
    FROM users
    WHERE role = 'ADMIN';

    -- Migrate MECHANIC role
    INSERT INTO user_group_memberships (user_id, group_id)
    SELECT id, mechanic_group_id
    FROM users
    WHERE role = 'MECHANIC';
END;
$function$

CREATE OR REPLACE FUNCTION public.notify_mechanics_about_remark()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
DECLARE
    mechanic_id uuid;
    room_id uuid;
BEGIN
    -- Create a chat room for the remark if it doesn't exist
    INSERT INTO chat_rooms (name, type)
    VALUES (
        'Remarque ' || NEW.id,
        'MECHANIC_REMARK'
    )
    RETURNING id INTO room_id;

    -- Add the remark creator to the room
    INSERT INTO chat_room_members (room_id, user_id)
    VALUES (room_id, NEW.user_id);

    -- Add all mechanics to the room
    FOR mechanic_id IN
        SELECT id FROM users WHERE role = 'MECHANIC'
    LOOP
        INSERT INTO chat_room_members (room_id, user_id)
        VALUES (room_id, mechanic_id);
    END LOOP;

    -- Create initial message
    INSERT INTO chat_messages (room_id, user_id, content)
    VALUES (
        room_id,
        NEW.user_id,
        'Nouvelle remarque sur ' || (SELECT registration FROM aircraft WHERE id = NEW.aircraft_id) || ': ' || NEW.content
    );

    RETURN NEW;
END;
$function$

CREATE OR REPLACE FUNCTION public.safe_delete_user(user_id uuid)
 RETURNS void
 LANGUAGE plpgsql
 SECURITY DEFINER
AS $function$
declare
  has_transactions boolean;
  has_entries boolean;
  has_assigned_entries boolean;
begin
  -- Vrifier les transactions
  select exists(
    select 1 from account_transactions 
    where user_id = $1
    limit 1
  ) into has_transactions;

  if has_transactions then
    raise exception 'Impossible de supprimer ce membre car il a des transactions associes';
  end if;

  -- Vrifier les entres comptables
  select exists(
    select 1 from account_entries 
    where user_id = $1
    limit 1
  ) into has_entries;

  if has_entries then
    raise exception 'Impossible de supprimer ce membre car il a des entres comptables';
  end if;

  -- Vrifier les entres comptables assignes
  select exists(
    select 1 from account_entries 
    where assigned_to_id = $1
    limit 1
  ) into has_assigned_entries;

  if has_assigned_entries then
    raise exception 'Impossible de supprimer ce membre car il est assign  des entres comptables';
  end if;

  -- Supprimer les donnes associes dans l'ordre correct
  delete from dismissed_announcements where user_id = $1;
  delete from chat_room_members where user_id = $1;
  delete from chat_messages where user_id = $1;
  delete from private_messages where sender_id = $1 or recipient_id = $1;
  delete from pilot_licenses where user_id = $1;
  delete from medical_certifications where user_id = $1;
  delete from user_badges where user_id = $1;
  delete from availabilities where user_id = $1;
  delete from aircraft_remark_responses where user_id = $1;
  delete from aircraft_remarks where user_id = $1;
  delete from user_group_memberships where user_id = $1;
  delete from club_members where user_id = $1;

  -- Supprimer l'utilisateur de public.users
  -- Le trigger sync_user_deletion se dclenchera automatiquement
  delete from users where id = $1;
end;
$function$

CREATE OR REPLACE FUNCTION public.set_current_timestamp_updated_at()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    NEW.updated_at = now();
    RETURN NEW;
END;
$function$

CREATE OR REPLACE FUNCTION public.trigger_set_updated_at()
 RETURNS trigger
 LANGUAGE plpgsql
AS $function$
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END;
$function$

